import React from 'react';

import './Card.css';

const Card = ({
    id,
    name,
    type,
    manaCost,
    text,
    flavor,
    imageUrl,
    addCardToDeck,
    removeCardFromDeck
}) => {
    return (
        <div className="Card">
            <div className="info">
                <p className="header">
                    {name} - {manaCost}
                </p>
                <p className="type">
                    {type}
                </p>
                <p className="text">
                    {text}
                </p>
                <p className="flavor">
                    {flavor}
                </p>
            </div>
            <img className="preview" src={imageUrl} />
            <div className="actions">
                <button class="glow-on-hover"
                    onClick={() => {
                        addCardToDeck({
                            id,
                            name
                        })
                    }}>
                    (+) Add
          </button>
                <button class="glow-on-hover"
                    onClick={() => {
                        removeCardFromDeck({
                            id
                        })
                    }}>
                    (-) Remove
        </button>
            </div>
        </div>
    );
}

export default Card;
