import axios from 'axios';
import './index.css';

export async function fetchCards({ name, text }) {
    try {
        const { data } = await axios.get(
            `https://api.magicthegathering.io/v1/cards?name=${name}&text=${text}`
        );

        return data.cards || [];
    } catch (error) {
        throw error;
    }
}

export async function fetchDropdown({ name }) {
    try {
        const { data } = await axios.get(
            `https://api.magicthegathering.io/v1/cards?name=${name}`
        );
        console.log({ name });
        return name
    } catch (error) {
        throw error;
    }
}
