import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { SearchBar, SearchResults, DeckList } from './components';
import './Style.css';


const App = () => {
    const [results, setResults] = useState([]);
    const [deck, setDeck] = useState([]);

    const addCardToDeck = ({ id, name }) => {
        const nextDeck = [...deck];
        const index = nextDeck.findIndex(card => card.id === id);

        if (index > -1) {
            nextDeck[index].count += 1;
        } else {
            nextDeck.push({
                id,
                name,
                count: 1
            });
        }

        setDeck(nextDeck);
    }

    const removeCardFromDeck = ({ id }) => {
        const nextDeck = [...deck];
        const index = nextDeck.findIndex(card => card.id === id);

        if (index === -1) {
            return;
        }

        if (nextDeck[index].count === 1) {
            nextDeck.splice(index, 1);
        } else {
            nextDeck[index].count -= 1;
        }

        setDeck(nextDeck);
    }

    return (
        <div id="app">
            <SearchBar setResults={setResults} />
            <SearchResults
                results={results}
                addCardToDeck={addCardToDeck}
                removeCardFromDeck={removeCardFromDeck} />
            <DeckList deck={deck} />
        </div>
    );
}

ReactDOM.render(
    <h1>Hello, World</h1>, //THIS IS JSX (allowing HTML) 
    document.getElementById('Header') //WILL PLACE H1 INSIDE OF ROOT
)


ReactDOM.render(
    <App />,
    document.getElementById('root')
);
