import axios from 'axios';
import './index.css';

export async function fetchCards({ name, text }) {
    try {
        const { data } = await axios.get(
            `https://api.magicthegathering.io/v1/cards?name=${name}&text=${text}`
        );

        return data.cards || [];
    } catch (error) {
        throw error;
    }
}

export async function fetchTypes({ types }) {
    try {
        const { data } = await axios.get(
            `https://api.magicthegathering.io/v1/types=${types}`
        );
        console.log(data)
        return data.searchTypes || [];
    } catch (error) {
        throw error;
    }
}
